import React, { Component } from 'react';
class AddTask extends Component {
  constructor(props) {
    super(props);

    this.state = {
        title: '',
        description: ''
      };

    this.drag = this.drag.bind(this);
  }

  addTask() {
    console.log(this.state.title);
  }

  drag(e) {
    e.preventDefault();
    e.dataTransfer.setData("id", e.target.id);
  }

  dragOver(e) {
    e.stopPropagation();
  }

  render() {
    return <div>
                <div class="new-task">
                    <input type="text" value={this.state.title} name="name" placeholder="Title"/>
                    <input type="text" value={this.state.description} name="description" placeholder="Description"/>
                </div>
                <div class="add-task-button" >Add Task</div>
            </div>
  }
}
export default AddTask;
