import React, { Component } from 'react';
import Card from './Card';

class Board extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: props.tasks
    };
  }

  drop() {
    e.preventDefault();
    var data = e.dataTransfer.getData("text");
    e.target.appendChild(document.getElementById(data));
  }

  allowDrop() {
    e.preventDefault();
  }

  render() {
    const cards = this.state.tasks.map((task) =>
    <Card key={task.id}
          name={task.name}
          description={task.description}></Card>
  );
    return <div class="board" ondrop={drop} ondragover={allowDrop}>{cards}</div>
  }
}
export default Board;
