import React, { Component } from 'react';
import Card from './Card';

class Board extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: props.tasks
    };
  }

  drop(e) {
    e.preventDefault();
    var data = e.dataTransfer.getData("text");
    e.target.appendChild(document.getElementById(data));
  }

  allowDrop(e) {
    e.preventDefault();
  }

  render() {
    const cards = this.state.tasks.map((task) =>
    <Card key={task.id}
          id={task.id}
          name={task.name}
          description={task.description}></Card>
  );
    return <div class="board" onDrop={(e) => this.drop(e)} onDragOver={(e) => this.allowDrop(e)}>{cards}</div>
  }
}
export default Board;
