import React, { Component } from 'react';
import Card from './Card';

class Board extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: props.tasks
    };
  }

  render() {
    const cards = this.state.tasks.map((task) =>
    <Card key={task.id}
          name={task.name}
          description={task.description}></Card>
  );
    return <div class="board" ondrop="drop(event)" ondragover="allowDrop(event)">{cards}</div>
  }
}
export default Board;
